import React, { useState } from "react";
import axios from "axios";
import * as XLSX from "xlsx";

const tableFields = { 
  client: ["name"],
  circle: ["name", "client"],
  cluster: ["name", "circle"],
  branch: ["sol_id", "branch_name", "city", "circle", "bank_name"],
  custodian: ["name"],
  driver: ["name"],
  vehicle: ["name"],
  user: ["name", "email", "password", "confirmPassword", "role"],
};

const AddTable = () => {
  const [selectedTable, setSelectedTable] = useState("");
  const [formData, setFormData] = useState({});
  const [errorMessage, setErrorMessage] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const [file, setFile] = useState(null);

  const handleTableChange = (e) => {
    setSelectedTable(e.target.value);
    setFormData({});
    setErrorMessage("");
    setSuccessMessage("");
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const resetForm = () => {
    setFormData({});
    setFile(null);
    setErrorMessage("");
    setSuccessMessage("");
  };

  const validateForm = () => {
    if (selectedTable === "user") {
      if (formData.password !== formData.confirmPassword) {
        setErrorMessage("❌ Password and Confirm Password do not match.");
        return false;
      }
    }
    return true;
  };

  const handleManualSubmit = async () => {
    if (!validateForm()) return;
    try {
      const res = await axios.post("/api/add-entry", {
        table: selectedTable,
        data: formData,
      });
      setSuccessMessage("✅ Entry added successfully!");
      setErrorMessage("");
      resetForm();
    } catch (error) {
      if (error.response && error.response.data?.duplicates) {
        setErrorMessage(
          `⚠️ Duplicate entries found: ${error.response.data.duplicates.join(", ")}`
        );
      } else {
        setErrorMessage("❌ Failed to add entry. Please check input.");
      }
    }
  };

  const handleFileUpload = (e) => {
    const uploadedFile = e.target.files[0];
    setFile(uploadedFile);
  };

  const handleFileSubmit = async () => {
    if (!file) {
      setErrorMessage("❌ Please select a file first.");
      return;
    }
    const formDataFile = new FormData();
    formDataFile.append("file", file);
    formDataFile.append("table", selectedTable);

    try {
      const res = await axios.post("/api/upload-file", formDataFile, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      setSuccessMessage(
        `✅ File uploaded successfully! ${res.data.insertedCount} rows inserted.`
      );
      setErrorMessage("");
      setFile(null);
    } catch (error) {
      if (error.response && error.response.data?.duplicates) {
        setErrorMessage(
          `⚠️ Duplicate entries in file: ${error.response.data.duplicates.join(", ")}`
        );
      } else {
        setErrorMessage("❌ File upload failed.");
      }
    }
  };

  return (
    <div className="p-4 max-w-2xl mx-auto bg-white shadow-lg rounded-lg">
      <h2 className="text-2xl font-bold mb-4">Add Table Entry</h2>

      <select
        value={selectedTable}
        onChange={handleTableChange}
        className="border p-2 rounded w-full mb-4"
      >
        <option value="">-- Select Table --</option>
        <option value="kms_report">KMS Report</option>
        <option value="activity_report">Activity Report</option>
        <option value="branch">Branch</option>
        <option value="custodian">Custodian</option>
        <option value="driver">Driver</option>
        <option value="vehicle">Vehicle</option>
        <option value="user">User</option>
      </select>

      {errorMessage && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-2 mb-4 rounded">
          {errorMessage}
        </div>
      )}
      {successMessage && (
        <div className="bg-green-100 border border-green-400 text-green-700 px-4 py-2 mb-4 rounded">
          {successMessage}
        </div>
      )}

      {selectedTable && tableFields[selectedTable].length > 0 && (
        <div>
          {tableFields[selectedTable].map((field) => (
            <div key={field} className="mb-3">
              <label className="block font-medium mb-1">
                {field === "confirmPassword"
                  ? "Confirm Password"
                  : field.charAt(0).toUpperCase() + field.slice(1)}
              </label>
              {field === "role" ? (
                <select
                  name={field}
                  value={formData[field] || ""}
                  onChange={handleInputChange}
                  className="border p-2 rounded w-full"
                >
                  <option value="">-- Select Role --</option>
                  <option value="Admin">Admin</option>
                  <option value="Manager">Manager</option>
                  <option value="User">User</option>
                </select>
              ) : (
                <input
                  type={
                    field.toLowerCase().includes("password")
                      ? "password"
                      : "text"
                  }
                  name={field}
                  value={formData[field] || ""}
                  onChange={handleInputChange}
                  className="border p-2 rounded w-full"
                />
              )}
            </div>
          ))}
          <div className="flex gap-3">
            <button
              onClick={handleManualSubmit}
              className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
            >
              Save Entry
            </button>
            <button
              onClick={resetForm}
              className="bg-yellow-500 text-white px-4 py-2 rounded hover:bg-yellow-600"
            >
              Reset
            </button>
          </div>
        </div>
      )}

      {selectedTable && (
        <div className="mt-6">
          <h3 className="text-lg font-semibold mb-2">Upload CSV/XLSX</h3>
          <input
            type="file"
            accept=".csv, .xlsx"
            onChange={handleFileUpload}
            className="mb-3"
          />
          <button
            onClick={handleFileSubmit}
            className="bg-purple-500 text-white px-4 py-2 rounded hover:bg-purple-600"
          >
            Upload File
          </button>
        </div>
      )}
    </div>
  );
};

export default AddTable;
