import React, { useState, useRef, useEffect } from "react";
import axios from "axios";
import * as XLSX from "xlsx";

const AddTable = () => {
  const [selectedTable, setSelectedTable] = useState("branch");
  const [formData, setFormData] = useState({});
  const [message, setMessage] = useState("");
  const fileInputRef = useRef(null);

  // Dropdown data for branch form and others
  const [clusters, setClusters] = useState([]);
  const [circles, setCircles] = useState([]);
  const [clients, setClients] = useState([]);

  useEffect(() => {
    const fetchDropdownData = async () => {
      try {
        const [clusterRes, circleRes, clientRes] = await Promise.all([
          axios.get("http://localhost:8888/api/tables/cluster"),
          axios.get("http://localhost:8888/api/tables/circle"),
          axios.get("http://localhost:8888/api/tables/client"),
        ]);
        setClusters(clusterRes.data || []);
        setCircles(circleRes.data || []);
        setClients(clientRes.data || []);
      } catch (error) {
        console.error("Error fetching dropdown data:", error);
      }
    };
    fetchDropdownData();
  }, []);

  const UNIQUE_FIELDS = {
    user: "email",
    branch: "sol_id",
    vehicle: "name",
    custodian: "name",
    driver: "name",
    cluster: "name",
    circle: "name",
    client: "name",
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleTableChange = (e) => {
    setSelectedTable(e.target.value);
    setFormData({});
    if (fileInputRef.current) {
      fileInputRef.current.value = null;
    }
    setMessage("");
  };

  const getFieldsForTable = () => {
    switch (selectedTable) {
      case "user":
        return ["name", "email", "password", "confirm_password", "role"];
      case "branch":
        return [
          "sol_id",
          "branch_name",
          "city",
          "cluster",
          "circle",
          "client",
          "bank_name",
        ];
      case "vehicle":
      case "custodian":
      case "driver":
      case "cluster":
      case "circle":
      case "client":
        return ["name"];
      default:
        return [];
    }
  };

  const readFileAsArrayBuffer = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onload = () => resolve(reader.result);
      reader.onerror = () => reject(reader.error);
      try {
        reader.readAsArrayBuffer(file);
      } catch (err) {
        reject(err);
      }
    });
  };

  const handleSubmit = async () => {
    try {
      setMessage("");

      const fileFromInput = fileInputRef.current?.files?.[0];

      if (fileFromInput) {
        let data;
        try {
          data = await readFileAsArrayBuffer(fileFromInput);
        } catch (e) {
          console.error("File reading error:", e);
          setMessage("File could not be read. Please reselect the file.");
          return;
        }

        const workbook = XLSX.read(data, { type: "array" });
        const sheetName = workbook.SheetNames[0];
        const worksheet = workbook.Sheets[sheetName];
        const json = XLSX.utils.sheet_to_json(worksheet);

        const uniqueField = UNIQUE_FIELDS[selectedTable];
        const uniqueValues = new Set();
        const filteredRows = [];
        const duplicateRows = [];

        for (const row of json) {
          const key = row[uniqueField];
          if (key == null || key === "") {
            duplicateRows.push({ reason: `Missing ${uniqueField}`, row });
          } else if (uniqueValues.has(key)) {
            duplicateRows.push({ reason: `Duplicate in file: ${key}`, row });
          } else {
            uniqueValues.add(key);
            filteredRows.push(row);
          }
        }

        if (filteredRows.length === 0) {
          setMessage("No valid rows found in file. All are missing or duplicate.");
          return;
        }

        const formData = new FormData();
        formData.append("file", fileFromInput);
        formData.append("table", selectedTable);

        const response = await axios.post(
          "http://localhost:8888/api/upload-file",
          formData,
          {
            headers: { "Content-Type": "multipart/form-data" },
          }
        );

        if (response.data?.inserted > 0) {
          setMessage(
            `${response.data.inserted} rows inserted successfully.` +
              (duplicateRows.length
                ? ` ${duplicateRows.length} duplicate rows were skipped.`
                : "")
          );
        } else {
          setMessage("No rows inserted. All rows might be duplicates.");
        }

        return;
      }

      // Manual form entry validation
      const uniqueKey = UNIQUE_FIELDS[selectedTable];

      if (!formData[uniqueKey]) {
        setMessage(`Please enter ${uniqueKey}`);
        return;
      }

      if (selectedTable === "user") {
        // Email validation
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        if (!formData.email || !emailRegex.test(formData.email)) {
          setMessage("Please enter a valid email address.");
          return;
        }
        // Password and confirm password validation
        if (!formData.password || !formData.confirm_password) {
          setMessage("Please enter password and confirm password.");
          return;
        }
        if (formData.password !== formData.confirm_password) {
          setMessage("Password and confirm password do not match.");
          return;
        }
        if (!formData.role) {
          setMessage("Please select a role.");
          return;
        }
      }

      // Prepare data to send (remove confirm_password)
      const dataToSend = { ...formData };
      delete dataToSend.confirm_password;

      const response = await axios.post("http://localhost:8888/api/add-entry", {
        table: selectedTable,
        data: dataToSend,
      });

      if (response.data.success) {
        setMessage("Entry added successfully.");
        setFormData({});
        if (fileInputRef.current) {
          fileInputRef.current.value = null;
        }
      } else if (response.data.duplicate) {
        setMessage(`Duplicate entry for ${uniqueKey}: ${formData[uniqueKey]}`);
      } else {
        setMessage("Failed to add entry.");
      }
    } catch (err) {
      console.error("Submit Error:", err);
      setMessage(
        err?.response?.data?.message ||
          "Unexpected error occurred while submitting."
      );
    }
  };

  return (
    <div style={{ maxWidth: "600px", margin: "20px auto" }}>
      <h2>Add Data to Table</h2>

      <label>
        Choose Table:
        <select
          value={selectedTable}
          onChange={handleTableChange}
          style={{ marginLeft: 10 }}
        >
          <option value="user">User</option>
          <option value="branch">Branch</option>
          <option value="vehicle">Vehicle</option>
          <option value="custodian">Custodian</option>
          <option value="driver">Driver</option>
          <option value="cluster">Cluster</option>
          <option value="circle">Circle</option>
          <option value="client">Client</option>
        </select>
      </label>

      <div style={{ marginTop: "20px" }}>
        <h4>Manual Entry:</h4>

        {getFieldsForTable().map((field) => {
          if (selectedTable === "user") {
            if (field === "role") {
              return (
                <div key={field} style={{ marginBottom: "10px" }}>
                  <label>{field}:</label>
                  <select
                    name="role"
                    value={formData.role || ""}
                    onChange={handleInputChange}
                    style={{ width: "100%", padding: 6 }}
                  >
                    <option value="">Select Role</option>
                    <option value="admin">Admin</option>
                    <option value="manager">Manager</option>
                    <option value="user">User</option>
                  </select>
                </div>
              );
            } else if (field === "password" || field === "confirm_password") {
              return (
                <div key={field} style={{ marginBottom: "10px" }}>
                  <label>{field.replace("_", " ")}:</label>
                  <input
                    type="password"
                    name={field}
                    value={formData[field] || ""}
                    onChange={handleInputChange}
                    style={{ width: "100%", padding: 6 }}
                    autoComplete="new-password"
                  />
                </div>
              );
            } else {
              return (
                <div key={field} style={{ marginBottom: "10px" }}>
                  <label>{field}:</label>
                  <input
                    type="text"
                    name={field}
                    value={formData[field] || ""}
                    onChange={handleInputChange}
                    style={{ width: "100%", padding: 6 }}
                  />
                </div>
              );
            }
          }

          // Branch dropdown fields
          if (
            selectedTable === "branch" &&
            (field === "cluster" || field === "circle" || field === "client")
          ) {
            let options = [];
            if (field === "cluster") options = clusters;
            else if (field === "circle") options = circles;
            else if (field === "client") options = clients;

            return (
              <div key={field} style={{ marginBottom: "10px" }}>
                <label>{field}:</label>
                <select
                  name={field}
                  value={formData[field] || ""}
                  onChange={handleInputChange}
                  style={{ width: "100%", padding: 6 }}
                >
                  <option value="">Select {field}</option>
                  {options.map((opt) => (
                    <option key={opt._id || opt.name} value={opt.name}>
                      {opt.name}
                    </option>
                  ))}
                </select>
              </div>
            );
          } else {
            // default text input for other fields
            return (
              <div key={field} style={{ marginBottom: "10px" }}>
                <label>{field}:</label>
                <input
                  type="text"
                  name={field}
                  value={formData[field] || ""}
                  onChange={handleInputChange}
                  style={{ width: "100%", padding: 6 }}
                />
              </div>
            );
          }
        })}

        <button
          onClick={handleSubmit}
          style={{
            marginTop: "10px",
            padding: "10px 16px",
            backgroundColor: "#007bff",
            color: "white",
            border: "none",
            borderRadius: 6,
            cursor: "pointer",
          }}
        >
          Submit
        </button>
      </div>

      <hr style={{ margin: "20px 0" }} />

      <div>
        <h4>OR Upload Excel File:</h4>
        <input type="file" accept=".xlsx, .xls" ref={fileInputRef} />
        <button
          onClick={handleSubmit}
          style={{
            marginTop: "10px",
            padding: "10px 16px",
            backgroundColor: "#28a745",
            color: "white",
            border: "none",
            borderRadius: 6,
            cursor: "pointer",
          }}
        >
          Upload
        </button>
      </div>

      {message && (
        <p
          style={{
            marginTop: "20px",
            color: message.startsWith("Failed") ? "red" : "green",
          }}
        >
          {message}
        </p>
      )}
    </div>
  );
};

export default AddTable;
